mapper.select:

select

from supervision
where sid = #{sid,jdbcType=INTEGER}

SELECT
d.DEPT_NAME,
s.dept_id
FROM
department AS d INNER JOIN
supervision AS s ON d.DEPT_ID=s.dept_id


    and status=#{status}


    and begin_time= #{beginTime}


    and end_time= #{endTime}


    and type_id=#{typeId}


    and dept_Id=#{deptId}

select (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=0

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}

) as count0 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=1

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count1 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=2

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count2 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=3

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count3 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=4

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count4 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=5

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count5 ,
 (select count(sp.sid) from supervision sp,user u where sp.manager_id=u.USER_ID AND u.DEPT_ID=#{deptId} and sp.status=6

     and sp.type_id=#{typeId}


     and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
   ) as count6

select (select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=0

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}

) as count0 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=1

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count1 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=2

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count2 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=3

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count3 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=4

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count4 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=5

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count5 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=6

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count6 from dual

select (select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=0

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}

) as count0 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=1

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count1 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=2

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count2 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=3

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count3 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=4

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count4 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=5

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
) as count5 ,
(select count((sp."SID")) from SUPERVISION sp,"USER" us where sp.manager_id=us.USER_ID AND us.DEPT_ID=#{deptId} and sp.status=6

  and sp.type_id=#{typeId}


  and sp.begin_time  BETWEEN #{beginDate}  AND  #{endDate}
