SELECT sp.sid, sp.type_id, sp.parent_id, sp.end_time, u_c.USER_NAME as creater_id, sp.leader_id, sp.manager_id as manager_id , sp.sup_name, u_L.USER_NAME, u_m.USER_NAME as manager_Name, st.type_name, if (sp.manager_id=#{managerId},1,0) as count0 , if ((sp.USER_ID LIKE concat('%,',#{userId},',%') OR sp.USER_ID LIKE concat(#{userId},',%')),1,0) as count1 , if (sp.leader_id=#{managerId},1,0) as count2 , sp.content, sp.status, sp.USER_ID FROM supervision_type st, supervision sp LEFT JOIN `user` u_L ON sp.leader_id=u_L.USER_ID left join `user` u_m ON sp.manager_id=u_m.USER_ID left join `user` u_c ON sp.creater_id=u_c.USER_ID WHERE st.sid=sp.type_id and (sp.manager_id=#{managerId} or (sp.USER_ID LIKE concat('%,',#{userId},',%') OR sp.USER_ID LIKE concat(#{userId},',%')) or sp.leader_id=#{userId}) and
sp.status in (1,2,4,5)
sp.status=#{status}

select 
(select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id  where  status=0
) as count0 ,
(select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=1
) as count1 ,
(select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=2
) as count2 ,
(select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=3
) as count3 ,
 (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=4
) as count4 ,
 (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=5
 ) as count5 ,
 (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id   where  status=6
 ) as count6

