select 
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where status=0

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

    ) as count0,
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where status=7

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

   ) as count1,
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where status=1

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

  ) as count2, 
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where  real_end_time > end_time AND  status=1

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

  ) as count3,
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where status=3

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

  ) as count4,
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where real_end_time > end_time AND status=6

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

   ) as count5 ,
  (select count(sid) from supervision LEFT JOIN user on supervision.manager_id=user.user_id where real_end_time <= end_time AND status=6

     and user.DEPT_ID = #{deptId}


     and type_id=#{typeId}


     and  begin_time >=  beginDate


     and end_time  <=  endDate

  ) as count6

